import React from "react";
import ReactDOM from "react-dom";
import ReactDataGrid from "react-data-grid";
import "./styles.css";
import axios from 'axios';

const columns = [
  { key: "01. symbol", name: "Company" },
  { key: "02. open", name: "Open"},
  { key: "03. high", name: "High"},
  {key: "04. low", name: "Low"},
  {key: "09. change", name: "Change"}
];

const rows = [
];

class Example extends React.Component {
  state = { rows };
  constructor(props){
      super(props);
      this.getstockdata();
  }

  onGridRowsUpdated = ({ fromRow, toRow, updated }) => {
    this.setState(state => {
      const rows = state.rows.slice();
      for (let i = fromRow; i <= toRow; i++) {
        rows[i] = { ...rows[i], ...updated };
      }
      return { rows };
    });
  };

  getstockdata() {
      axios.get("http://api.samair.xyz/quotes/VMW")
      .then((rep) => {
          console.log(rep.data["01 symbol"])
      }

      )
  }

  render() {
    return (
        <div style = {{display:"flex"}}>
      <ReactDataGrid
        left = {'10'}
        height={'auto'}
        width = {'94%'}
        columns={columns}
        rowGetter={i => this.state.rows[i]}
        rowsCount={3}
        onGridRowsUpdated={this.onGridRowsUpdated}
        enableCellSelect={true}
      />
      </div>
    );
  }
}

export default Example;
