import React, { Component } from 'react';
import './App.css';
import DenseAppBar from './ApplicationBar/AppBar';
import Inputs from './ApplicationBar/SearchButton';
import { Input } from '@material-ui/core';

class App extends Component {
  constructor(props){
    super(props);
  this.state = {
    companies : ["Mohit"]
  }
  }
  addForMonitor = (companyInfo) => {
      this.setState(prevState => (
        {
          companies : prevState.companies.concat(companyInfo)
        }
      ))
  }

  render() {
    return (
      <div>
      <DenseAppBar />
      <Inputs handleClick = {this.addForMonitor}/>
      </div>
    );
  }
}

export default App;
