import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Input from '@material-ui/core/Input';


export class Inputs extends Component{
    constructor(props){
        super(props)
    }
    state = {
        coName : ""
    }
    
    render(){
        return(
            <div style={{textAlign : "center"}}>
                <Input placeholder="Enter the Company Name"
                 type="search"
                 style={{width : "20%"}}
                 onChange = {(event) => {this.setState({coName : event.target.value})}}
                      />
                <Button size="small"
                 variant="outlined"
                 color="primary"
                 style={{margin:"4px"}}
                 onClick = {(e)=> this.props.handleClick()}
                 >Monitor</Button>
          </div>
        );
    }

}

export default Inputs;
