import React, { Component } from 'react';
import './App.css';
import DenseAppBar from './ApplicationBar/AppBar';
import Inputs from './ApplicationBar/SearchButton';
import Example from './DataGrid/stock-dg.js'
import axios from 'axios'
import swal from 'sweetalert'
import CircularProgress from '@material-ui/core/CircularProgress';
import classes from '*.module.scss';

class App extends Component {
  constructor(props){
    super(props);
  this.state = {
    companies : [],
    circle : false
  }
  }
  addForMonitor = (companyInfo) => {
      this.setState(prevState => ({ circle : true}))
      axios.get(`http://api.samair.xyz/quotes/${companyInfo}`)
      .then((rep) =>{
          if(rep.data["01. symbol"] !== undefined) {
            this.setState(prevState => ({
                  companies : this.state.companies.concat(rep.data)
            }))
          }
          else {

              swal ( "Oops" , `${companyInfo} Stock Doesn't Exists` ,  "error" )

          }
      })}



  render() {
    return (
      <div>
      <DenseAppBar />
      <Inputs handleClick = {this.addForMonitor}/>
      { this.state.companies.length === 0 ? console.log("Not Applicable") : <Example list = {this.state.companies}/>}
      { this.state.circle === false ? console.log("Not Applicable") : <CircularProgress variant="determinate" thickness="3.6" className = {classes.progress}/>}
      </div>
    );
  }
}

export default App;
